using System;
using System.IO;
using System.Reflection;
using System.Collections.Generic;
using VipleManagement.Core;

namespace VipleManagement.Resources
{
    public static class FontResources
    {
        // Liste des polices incorporées
        private static readonly Dictionary<string, byte[]> EmbeddedFonts = new Dictionary<string, byte[]>
        {
            // Roboto - Regular
            { "Roboto-Regular.ttf", Convert.FromBase64String("") },
            
            // Roboto - Bold
            { "Roboto-Bold.ttf", Convert.FromBase64String("") },

            // Open Sans - Regular
            { "OpenSans-Regular.ttf", Convert.FromBase64String("") },

            // JetBrains Mono - Regular
            { "JetBrainsMono-Regular.ttf", Convert.FromBase64String("") },

            // Hack - Regular (Libre programming font)
            { "Hack-Regular.ttf", Convert.FromBase64String("") }
        };

        // Extraire toutes les polices dans le dossier viple_res2/fonts
        public static void ExtractAllFonts()
        {
            try
            {
                // S'assurer que le dossier existe
                string fontDir = Path.Combine("viple_res2", "fonts");
                Directory.CreateDirectory(fontDir);

                // Extraire chaque police
                foreach (var fontEntry in EmbeddedFonts)
                {
                    string fontPath = Path.Combine(fontDir, fontEntry.Key);
                    
                    // Ne pas écraser le fichier s'il existe déjà
                    if (!File.Exists(fontPath))
                    {
                        File.WriteAllBytes(fontPath, fontEntry.Value);
                        LogManager.LogAction($"Police extraite: {fontEntry.Key}");
                    }
                }
                
                LogManager.LogAction("Toutes les polices ont été extraites avec succès");
            }
            catch (Exception ex)
            {
                LogManager.LogError($"Erreur lors de l'extraction des polices: {ex.Message}", ex);
            }
        }
    }
}